mapper.sql:
s_hashtag h, sm_ht2StringId m, s_reply p where h.id in ( SELECT id FROM s_hashtag START with hashtag in
#{aWord}
CONNECT BY PRIOR id = super_id) and h.id = m.hashtag_id and m.opponent_type = 'Post' and m.opponent_id = p.id and p.board_id = #{boardId} and descrim = 'post'

mapper.select:
	
	select p.*, w.name w_name, w.descrim w_descrim	 
	from(  
	 	select p.*, pc.reply_cnt	
			from (
				select p.id, count(r.id) reply_cnt	 
				from(  
					  SELECT * 
					    FROM s_reply
					   where board_id = #{boardId}
					   ORDER BY id DESC
					   OFFSET  (#{cri.pageNumber} - 1) * #{cri.amount}  rows fetch first #{cri.amount} rows only
				 	) p left outer join s_reply r 
					on r.id like p.id || '%'
				and length(r.id) > length(p.id)
				group by p.id) pc, s_reply p
			where pc.id = p.id
			ORDER BY p.id DESC	
	 		) p, s_party w
	where p.writer_id = w.user_id

	select p.*, w.name w_name, w.descrim w_descrim 	 
	  from(  
	 		select p.*, pc.reply_cnt	
		      from(
		  		   select p.id, count(r.id) reply_cnt	 	 
					 from(  
				  		  select p.id  
						    from
  				 	       ORDER BY id DESC	
					      OFFSET  (#{cri.pageNumber} - 1) * #{cri.amount}  rows fetch first #{cri.amount} rows only
				 		 ) p left outer join s_reply r 
					  on r.id like p.id || '%'
					 and length(r.id) > length(p.id)
					group by p.id
					) pc, s_reply p 
				where pc.id = p.id
		 	  ORDER BY p.id DESC	
		  ) p, s_party w
	  where p.writer_id = w.user_id
	  ORDER BY p.id DESC

